import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Alert, Typography, Box, Button, TextField, AppBar, Toolbar, Grid } from "@mui/material";
import CheckIcon from '@mui/icons-material/Check'; // Ícone de confirmação

const Cadastro = () => {
  const [view, setView] = useState(""); // "cliente", "atendente" ou vazio
  const [formData, setFormData] = useState({
    nome: "",
    cnpj: "",
    cpf: "",
    email: "",
    senha: "",
  });
  const [cnpjValid, setCnpjValid] = useState(null); // Estado para validação de CNPJ
  const [alertMessage, setAlertMessage] = useState(""); // Estado para mensagem do alerta
  const navigate = useNavigate(); // Inicializando o hook useNavigate

  // Animação dos containers
  const containerVariant = {
    hidden: { opacity: 0, scale: 0.9 },
    visible: { opacity: 1, scale: 1, transition: { duration: 0.5 } },
    exit: { opacity: 0, scale: 0.9, transition: { duration: 0.5 } },
  };

  const handleBack = () => {
    setView(""); // Voltar à tela inicial
    setFormData({ nome: "", cnpj: "", cpf: "", email: "", senha: "" });
    setCnpjValid(null); // Resetar o estado de validação do CNPJ
    setAlertMessage(""); // Limpar a mensagem do alerta
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Validação de CNPJ no campo de CNPJ (somente para atendentes)
    if (name === "cnpj" && value.length === 14) {
      axios.get(`https://api.cnpja.com/office/${value}`, {
        headers: { Authorization: '507f5121-2175-4925-8b5b-5f4ff17b3312-3165bba4-7fe0-461b-8092-5b90c99c4088' }
      })
        .then((response) => {
          setCnpjValid(true); // CNPJ válido
          setAlertMessage("CNPJ válido!");
        })
        .catch((error) => {
          setCnpjValid(false); // CNPJ inválido
          setAlertMessage("CNPJ inválido!");
        });
    }
  };

  const handleSubmit = async (type) => {
    let formDataToSend = { ...formData };

    // Remover CPF se for atendente, e remover CNPJ se for cliente
    if (type === "Atendente") {
      delete formDataToSend.cpf; // Não envia CPF para atendente
    } else if (type === "Cliente") {
      delete formDataToSend.cnpj; // Não envia CNPJ para cliente
    }

    const url =
      type === "Atendente"
        ? "http://127.0.0.1:8000/api/atendentes/cadastrar/"
        : "http://127.0.0.1:8000/api/clientes/cadastrar/";

    console.log("FormData:", formDataToSend); // Log para verificar os dados enviados

    try {
      const response = await axios.post(url, formDataToSend);
      alert(`${type} registrado com sucesso!`);
      console.log("Response:", response.data);
      handleBack(); // Resetar o formulário
      navigate("/login");
    } catch (error) {
      console.error("Erro ao enviar:", error.response?.data || error.message);
      alert("Erro ao registrar. Verifique os dados e tente novamente.");
    }
  };

  return (
    <Box sx={styles.container}>
      {/* Navbar */}
      <AppBar position="fixed" sx={styles.navbar}>
        <Toolbar>
          <Box sx={styles.logo}>
            <Typography variant="h6" sx={styles.logoText}>
              Farmacinha
            </Typography>
          </Box>
          <Box sx={styles.navLinks}>
            <Button component={RouterLink} to="/" sx={styles.navButton}>
              Início
            </Button>
            <Button component={RouterLink} to="/cadastro" sx={styles.navButton}>
              Cadastre-se
            </Button>
            <Button component={RouterLink} to="/login" sx={styles.navButton}>
              Entrar
            </Button>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Conteúdo */}
      <Box sx={styles.content}>
        <AnimatePresence>
          {/* Título e Subtítulo */}
          <motion.div
            style={styles.titleContainer}
            variants={containerVariant}
            initial="hidden"
            animate="visible"
            exit="exit"
          >
            <Typography variant="h3" sx={styles.mainTitle}>
              Farmacinha
            </Typography>
            <Typography variant="h5" sx={styles.subtitle}>
              Formulário de Cadastro
            </Typography>
          </motion.div>

          {/* Tela de Escolha */}
          {view === "" && (
            <motion.div
              style={styles.choiceContainer}
              variants={containerVariant}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <Typography variant="h4" sx={styles.title}>
                Você quer se registrar como?
              </Typography>
              <Box sx={styles.options}>
                <Button
                  variant="contained"
                  color="primary"
                  sx={styles.optionButton}
                  onClick={() => setView("atendente")}
                >
                  Atendente
                </Button>
                <Button
                  variant="contained"
                  color="secondary"
                  sx={styles.optionButton}
                  onClick={() => setView("cliente")}
                >
                  Cliente
                </Button>
              </Box>
            </motion.div>
          )}

          {/* Formulário de Atendente */}
          {view === "atendente" && (
            <motion.div
              style={styles.formContainer}
              variants={containerVariant}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <Typography variant="h5" sx={styles.formTitle}>
                Registro de Atendente
              </Typography>
              <TextField
                label="Nome Completo"
                name="nome"
                value={formData.nome}
                onChange={handleChange}
                variant="outlined"
                fullWidth
                sx={styles.input}
              />
              <TextField
                label="CNPJ"
                name="cnpj"
                value={formData.cnpj}
                onChange={handleChange}
                variant="outlined"
                fullWidth
                sx={styles.input}
                inputProps={{ maxLength: 14 }}
              />
              {alertMessage && (
                <Alert
                  icon={<CheckIcon fontSize="inherit" />}
                  severity={cnpjValid ? "success" : "error"}
                  sx={{ width: "100%", marginBottom: "15px" }}
                >
                  {alertMessage}
                </Alert>
              )}
              <TextField
                label="E-mail"
                name="email"
                value={formData.email}
                onChange={handleChange}
                variant="outlined"
                fullWidth
                sx={styles.input}
              />
              <TextField
                label="Senha"
                name="senha"
                type="password"
                value={formData.senha}
                onChange={handleChange}
                variant="outlined"
                fullWidth
                sx={styles.input}
              />
              <Box sx={styles.btnGroup}>
                <Button variant="contained" color="secondary" sx={styles.btnCancel} onClick={handleBack}>
                  Voltar
                </Button>
                <Button
                  variant="contained"
                  color="primary"
                  sx={styles.btnSubmit}
                  onClick={() => handleSubmit("Atendente")}
                  disabled={!cnpjValid}
                >
                  Registrar
                </Button>
              </Box>
            </motion.div>
          )}

          {/* Formulário de Cliente */}
          {view === "cliente" && (
            <motion.div
              style={styles.formContainer}
              variants={containerVariant}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <Typography variant="h5" sx={styles.formTitle}>
                Registro de Cliente
              </Typography>
              <TextField
                label="Nome Completo"
                name="nome"
                value={formData.nome}
                onChange={handleChange}
                variant="outlined"
                fullWidth
                sx={styles.input}
              />
              <TextField
                label="CPF"
                name="cpf"
                value={formData.cpf}
                onChange={handleChange}
                variant="outlined"
                fullWidth
                sx={styles.input}
                inputProps={{ maxLength: 11 }}
              />
              <TextField
                label="E-mail"
                name="email"
                value={formData.email}
                onChange={handleChange}
                variant="outlined"
                fullWidth
                sx={styles.input}
              />
              <TextField
                label="Senha"
                name="senha"
                type="password"
                value={formData.senha}
                onChange={handleChange}
                variant="outlined"
                fullWidth
                sx={styles.input}
              />
              <Box sx={styles.btnGroup}>
                <Button variant="contained" color="secondary" sx={styles.btnCancel} onClick={handleBack}>
                  Voltar
                </Button>
                <Button
                  variant="contained"
                  color="primary"
                  sx={styles.btnSubmit}
                  onClick={() => handleSubmit("Cliente")}
                >
                  Registrar
                </Button>
              </Box>
            </motion.div>
          )}
        </AnimatePresence>
      </Box>
    </Box>
  );
};

